{% from 'common/libs/host_config_queries.sls' import get_system_host_primary_user_posix_home with context %}
{% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import locate_dynamic_build_descriptor with context %} {% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import update_dynamic_build_descriptor with context %}
{% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import common_build_script_header with context %} {% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import common_build_script_footer with context %}
####################################################################### # Script header
{{ common_build_script_header(job_config, job_environ) }}
####################################################################### # Locate dynamic build descriptor.
{{ locate_dynamic_build_descriptor(job_config, job_environ) }}
# At this point (before checking out build branches), we already # know that recovery would require at least checking out recovery # branches back. The recovery and build branches are already written # in the dyn build desc - use it as initial recovery descriptor. cp "${LATEST_DYN_BUILD_DESC_PATH}" "${RECOVERY_DYN_BUILD_DESC_PATH}"
{% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import locate_repository_dynamic_build_descriptor with context %} {{ locate_repository_dynamic_build_descriptor(job_config, job_environ) }} ls -lrt "${REPO_DYN_BUILD_DESC_PATH}"
####################################################################### # Create restore point - commits of all local modifications. # # This restore point will be used to recover all local modifications # after switching back to recovery branches even if build branches # are supposed to be deleted. # # TODO: This script section is very similar (or the same) with what # has to be done when any changes need to be checked in during # the build pipeline. Factor out for reuse. # # Sequence: # - Save parent repo first # (as commits in childs will not make original restore point). # - Save all other repos (children).
# TODO: Use repository roles instead of `parent_repo_name`. # TODO: Create macros to provide commit order everything then parent. {% if pillar['parent_repo_name'] %} # Parent repo is defined and it is the first. {% set repo_list = [ pillar['parent_repo_name'] ] + pillar['system_features']['deploy_environment_sources']['source_repositories'].keys() %} {% else %} # This profile does not define parent repo. {% set repo_list = pillar['system_features']['deploy_environment_sources']['source_repositories'].keys() %} {% endif %}
{% for repo_name in repo_list %} {% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_name]['git'] %} REPO_PATH="{{ get_system_host_primary_user_posix_home(repo_config['source_system_host']) }}/{{ repo_config['origin_uri_ssh_path'] }}"
cd "${REPO_PATH}"
CURRENT_BRANCH="$(git rev-parse --abbrev-ref HEAD)" # HEAD value means that repository is at detached head. test "${CURRENT_BRANCH}" != "HEAD"
# Switch to "detached HEAD" to make restore point. # Checking out commit id makes it "detached HEAD". # TODO: This can be improved using either temporary branches or stash. DETACHED_HEAD_COMMIT_ID="$(git rev-parse --verify HEAD)" git checkout "${DETACHED_HEAD_COMMIT_ID}" DETACHED_HEAD="$(git rev-parse --abbrev-ref HEAD)" test "${DETACHED_HEAD}" == "HEAD" # NOTE: The build branch will have to be fast-forward merged # into the commit done in this "detached HEAD" next # (and recorded in `RESTORE_POINT_COMMIT`) # if no parent dynamic build descriptor is used.
{% if repo_name in pillar['system_features']['deploy_environment_sources']['repository_roles']['build_history_role'] %}
# NOTE: Do not create restore point in `build_history_role` purposefully.
{% else %}
# NOTE: Without `add -all` `diff-index` will not notice untracked files. git add --all git status # Commit only if there are changes. # Ignore dirty content in submodules because there is no way to # commit them from top-level repo anyway (and all dirty content # will be committed by children). git diff-index --ignore-submodules=dirty --exit-code HEAD || git commit --author "${GIT_AUTHOR_EMAIL}" -m 'Auto-commit: restore point commit' RESTORE_POINT_COMMIT="$(git rev-parse --verify HEAD)" echo "$RESTORE_POINT_COMMIT" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "restore_point_commit_ids:{{ repo_name }}"
# NOTE: Do not record `latest_commit_ids` here. # Do it later when it is decided that it is part # of the build branch.
{% endif %}
# Now check out `CURRENT_BRANCH` back. git checkout "${CURRENT_BRANCH}" CURRENT_BRANCH="$(git rev-parse --abbrev-ref HEAD)" # It still should point to `RECOVERY_BRANCH`. RECOVERY_BRANCH="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "recovery_branches:{{ repo_name }}")" test "${CURRENT_BRANCH}" == "${RECOVERY_BRANCH}"
cd -
{% endfor %}
####################################################################### # Update recovery descriptor.
# The subsequent steps generate new commits to repositories. # It is important to save captured restore points now without # waiting until end of the script because script may fail # and no captured restore points will be available for recovery. # Use current job dyn build desc as updated recovery descriptor. cp "${JOB_DYN_BUILD_DESC_PATH}" "${RECOVERY_DYN_BUILD_DESC_PATH}"
####################################################################### # Branch all repositories based on dynamic build descriptor.
{% for repo_name in pillar['system_features']['deploy_environment_sources']['source_repositories'].keys() %} {% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_name]['git'] %} REPO_PATH="{{ get_system_host_primary_user_posix_home(repo_config['source_system_host']) }}/{{ repo_config['origin_uri_ssh_path'] }}"
cd "${REPO_PATH}"
CURRENT_BRANCH="$(git rev-parse --abbrev-ref HEAD)" # HEAD value means that repository is at detached head. test "${CURRENT_BRANCH}" != "HEAD"
BUILD_BRANCH="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "build_branches:{{ repo_name }}")" REQUIRED_COMMIT_ID="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "required_commit_ids:{{ repo_name }}")" test "${CURRENT_BRANCH}" != "${BUILD_BRANCH}" git checkout -b "${BUILD_BRANCH}" "${REQUIRED_COMMIT_ID}"
{% if repo_name in pillar['system_features']['deploy_environment_sources']['repository_roles']['build_history_role'] %}
# NOTE: There is no restore points for `build_history_role`.
{% else %}
if [ "${PARENT_BUILD_TITLE}" != "_" ] then # Nothing to do. The restore point should not be part # of the build branch. echo "Switch to parent build branches without including restore point."
# NOTE: The recovery commits are skipped altogether. # They are not recorded in the `latest_commit_ids` because # they are irrelevant for the build - the initial # HEADs were switched into branches # from parent dyn build desc.
else # NOTE: Fast-forward merge newly created build branch to # `RESTORE_POINT_COMMIT` because we are using # current HEADs of all branches at the start of # the pipeline. RESTORE_POINT_COMMIT="$(python "${KEY_GETTER_PYTHON_SCRIPT}" "${JOB_DYN_BUILD_DESC_PATH}" "restore_point_commit_ids:{{ repo_name }}")" git merge --ff-only "${RESTORE_POINT_COMMIT}"
# Record `latest_commit_ids`. CURRENT_COMMIT_ID="$(git rev-parse --verify HEAD)" test "${CURRENT_COMMIT_ID}" == "${RESTORE_POINT_COMMIT}" echo "${CURRENT_COMMIT_ID}" | python "${KEY_SETTER_PYTHON_SCRIPT}" "${JOB_DYN_BUILD_DESC_PATH}" "latest_commit_ids:{{ repo_name }}"
fi
{% endif %}
cd -
{% endfor %}
####################################################################### # Commit versions of dynamic build descriptor # # - Version 1 `init_pipeline.start_new_build`. # - Version 2 `init_pipeline.describe_repositories_state`. # - Version 3 `init_pipeline.create_build_branches`.
{% set project_name = pillar['project_name'] %}
{% from 'common/libs/repo_config_queries.lib.sls' import get_repository_id_by_role with context %} {% set repo_id = get_repository_id_by_role('build_history_role') %} {% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_id]['git'] %}
JENKINS_DIR_PATH="{{ job_environ['jenkins_dir_path'] }}"
# At the moment simply save it into repository. for JOB_NAME in start_new_build describe_repositories_state create_build_branches do
{% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_id]['git'] %} REPO_PATH="{{ get_system_host_primary_user_posix_home(repo_config['source_system_host']) }}/{{ repo_config['origin_uri_ssh_path'] }}"
cd "${REPO_PATH}"
CURRENT_BRANCH="$(git rev-parse --abbrev-ref HEAD)" # HEAD value means that repository is at detached head. test "${CURRENT_BRANCH}" != "HEAD"
BUILD_BRANCH="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "build_branches:{{ repo_id }}")" test "${CURRENT_BRANCH}" == "${BUILD_BRANCH}"
VER_DYN_BUILD_DESC_PATH="{{ job_environ['jenkins_dir_path'] }}/build_pipeline/init_pipeline.${JOB_NAME}.dynamic_build_descriptor.yaml" cp "${VER_DYN_BUILD_DESC_PATH}" "${REPO_DYN_BUILD_DESC_PATH}" git add --all git status # Note that we don't check whether there are changes to commit. # There must be changes - each version of # dynamic build descriptor should be different. git commit --author "${GIT_AUTHOR_EMAIL}" -m "Auto-commit: dynamic build descriptor at ${JOB_NAME}"
# Record `latest_commit_ids`. CURRENT_COMMIT_ID="$(git rev-parse --verify HEAD)" echo "$CURRENT_COMMIT_ID" | python "${KEY_SETTER_PYTHON_SCRIPT}" "${JOB_DYN_BUILD_DESC_PATH}" "latest_commit_ids:{{ repo_name }}"
cd -
done
####################################################################### # Update dynamic build descriptor.
{{ update_dynamic_build_descriptor(job_config, job_environ) }}
# One of the purposes of this job is to set recovery # dynamic build descriptor so that changes made to repositories # can be reverted to their state before this job. cp "${LATEST_DYN_BUILD_DESC_PATH}" "${RECOVERY_DYN_BUILD_DESC_PATH}"
