{% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import locate_dynamic_build_descriptor with context %} {% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import update_dynamic_build_descriptor with context %}
{% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import common_build_script_header with context %} {% from 'common/jenkins/configure_jobs_ext/common_xml_templates.lib.sls' import common_build_script_footer with context %}
####################################################################### # Script header
{{ common_build_script_header(job_config, job_environ) }}
####################################################################### # Locate dynamic build descriptor.
{{ locate_dynamic_build_descriptor(job_config, job_environ) }}
####################################################################### # Capture into dynamic build descriptor: # - current branch names # - current commit ids # - build branch names # - status of local changes
# NOTE: We assume that repository is available # locally on Jenkins slave.
{% for repo_name in pillar['system_features']['deploy_environment_sources']['source_repositories'].keys() %} {% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_name]['git'] %} REPO_PATH="{{ get_system_host_primary_user_posix_home(repo_config['source_system_host']) }}/{{ repo_config['origin_uri_ssh_path'] }}"
cd "${REPO_PATH}" CURRENT_BRANCH="$(git rev-parse --abbrev-ref HEAD)" # HEAD value means that repository is at detached head. test "$CURRENT_BRANCH" != "HEAD" CURRENT_COMMIT_ID="$(git rev-parse --verify HEAD)" CURRENT_COMMIT_ID_SHORT="$(git rev-parse --short --verify HEAD)" BUILD_BRANCH="${CURRENT_BRANCH}-${CURRENT_COMMIT_ID_SHORT}-${BUILD_TITLE}" UNTRACKED_ITEMS="$(git ls-files --other --exclude-standard --directory | wc -l)" MODIFIED_ITEMS="$((git diff --name-only --cached --ignore-submodules=dirty ; git diff --name-only --ignore-submodules=dirty) | sort -u | wc -l)" cd -
if [ "${USE_INITIAL_SOURCES_FROM_BUILD_TITLE}" != "_" ] then echo "preserve existing initial branch and commit id" # The values must be set for this step to succeed. REQUIRED_BRANCH="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "initial_branches:{{ repo_name }}")" REQUIRED_COMMIT_ID="$(python ${KEY_GETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "initial_commit_ids:{{ repo_name }}")" else # Trivial case. REQUIRED_BRANCH="${CURRENT_BRANCH}" REQUIRED_COMMIT_ID="${CURRENT_COMMIT_ID}" fi
echo "$CURRENT_BRANCH" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "initial_branches:{{ repo_name }}" echo "$CURRENT_COMMIT_ID" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "initial_commit_ids:{{ repo_name }}" echo "$REQUIRED_BRANCH" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "required_branches:{{ repo_name }}" echo "$REQUIRED_COMMIT_ID" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "required_commit_ids:{{ repo_name }}" echo "$BUILD_BRANCH" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "build_branches:{{ repo_name }}" echo "$UNTRACKED_ITEMS" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "untracked_items:{{ repo_name }}" echo "$MODIFIED_ITEMS" | python ${KEY_SETTER_PYTHON_SCRIPT} ${JOB_DYN_BUILD_DESC_PATH} "modified_items:{{ repo_name }}"
{% endfor %}
####################################################################### # Verify: # All repositories are actually different directories # (no symlinks from one repository to another). # This is required as switching to build branches and restoring # them will unnecessarily complicate the procedure reducing # overall trust in it.
DIR_FS_ID_LIST_FILE="$(mktemp)" {% for repo_name in pillar['system_features']['deploy_environment_sources']['source_repositories'].keys() %} {% set repo_config = pillar['system_features']['deploy_environment_sources']['source_repositories'][repo_name]['git'] %} REPO_PATH="{{ get_system_host_primary_user_posix_home(repo_config['source_system_host']) }}/{{ repo_config['origin_uri_ssh_path'] }}" stat "${REPO_PATH}/." | grep '^Device:' >> "${DIR_FS_ID_LIST_FILE}" {% endfor %}
# If there is at least one duplicated line, line is not zero lenght. test -z "$(cat "${DIR_FS_ID_LIST_FILE}" | sort | uniq -d)"
####################################################################### # Update dynamic build descriptor.
{{ update_dynamic_build_descriptor(job_config, job_environ) }}
####################################################################### # Script footer
{{ common_build_script_footer(job_config, job_environ) }}
#######################################################################
